const widgetCss = css`
  .main-container {
    background: #262b37;
    max-height: 70px;
    overflow: hidden;
    margin: 0 10px;
  }

  .text-wrapper {
    display: flex;
    flex-direction: column;
    self-align: center;
    padding: 10px;
  }

  .workspace-title--container {
    width: 100%;
    height: 70px;
  }
  .workspace-title--container .image {
    width: 100px;
    float: left;
  }

  .workspace-title--container .title {
    width: calc(100% - 100px);
    height: 26px;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: #cdd2dd;
    font-size: 20px;
    float: left;
    overflow: hidden;
  }

  /* application title style if no customer label is selected */

  .single-title {
    padding-top: 19px;
  }

  /* Customer label */

  .label-text {
    color: #cdd2dd;
    font-size: 16px;
    line-height: 18px;
    margin: 0;
    text-transform: capitalize;
    text-overflow: ellipsis;
    width: 100%;
  }
`;

const resolvePromise = directive(promise => part => {
  Promise.resolve(promise).then(resolvedValue => {
    part.setValue(resolvedValue);
    part.commit();
  });
});

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }
  initialize() {
    super.initialize();
    this.selectedName = ''; // defaults to no customer selected

  }

  async getApp(appId) {
    return fetch(`${location.origin}/api/app/${appId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  async getRecord(appId, recordId) {
    return fetch(`${location.origin}/api/app/${appId}/record/${recordId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  async getCustomerName() {
    const record = await this.getRecord(CKB_APP_ID, this.record[FIELD_KEY]);
    return record.values[CKB_CUSTOMER_NAME_FIELD_ID]
  }

  render() {
    return resolvePromise(this.template());
  }

  async template() {
    const customer_name = await this.getCustomerName();
    return html`
      <div class="main-container">
        <div class="workspace-title--container">
          <div class="image">${backgroundImg}</div>
          <div class="${customer_name ? 'text-wrapper' : 'single-title'}">
            <div class="title">${APPLICATION_TITLE}</div>
            ${customer_name
              ? html`
                <div class="text-label-container">
                    <div class="label-text">${customer_name}</div>
                </div>
              </div>`
              : html``}
          </div>
        </div>
      </div>
    `;
  }
}

/*
 * slim-severity-level-dropdown v2.0.0
 * https://swimlane.github.io/custom-widgets/slim-severity-level-dropdown/
 */

import { SwimlaneElement, css, html, svg } from '@swimlane/swimlane-element@1';
import { repeat } from 'lit-html@1/directives/repeat.js';

const FIELD_KEY = 'case_severity';
const TITLE = 'Severity';
const SIZE = 'large'; // 'small' or 'large'

// Define color vars of each dropdown option
// Names should match with field's option names
const dropdownOptions = [
  {
    name: 'Unknown',
    color: '#1483FF'
  },
  {
    name: 'Informational',
    color: '#00B862'
  },
  {
    name: 'Low',
    color: '#AFDF0A'
  },
  {
    name: 'Medium',
    color: '#F3E562'
  },
  {
    name: 'High',
    color: '#FF5722'
  },
  {
    name: 'Critical',
    color: '#FF4514'
  }
];

const arrowIcon = svg`
<svg width="12" height="8" viewBox="0 0 12 8" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M1 1.5L6 6.5L11 1.5" stroke="#818FA9" stroke-width="2"/>
</svg>
`;

const checkboxIcon = svg`
<svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M4.8125 12.25L0 7.4375L2.1875 5.16797L5.25 8.09375L12.0312 1.3125L14 3.28125L4.8125 12.25Z" fill="#1483FF"/>
</svg>
`;

const widgetCss = css`
  .slim-widget--container {
    max-height: 70px;
    width: 100%;
    position: relative;
  }
  .dropdown--container {
    width: 100%;
    display: flex;
    align-items: center;
  }

  /* select style */
  .selected--container {
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
    box-sizing: border-box;
    background: #262B37;
  }

  /* style for large container  */
  .selected--container.large {
    height: 70px;
  }
  .selected--container.small {
    height: 40px;
  }
  .selected--container .selected {
    height: 100%;
    width: calc(100% - 30px);
    display: flex;
    align-items: center;
  }

  /* color circle */
  .indicator {
    width: 10px;
    height: 10px;
    min-width: 10px;
    border: var(--indicatorColor);
    border-radius: 50%;
    background: var(--indicatorColor);
    margin-left: 10px;
    align-self: center;
  }
  .text-label-container {
    display: flex;
    flex-direction: column;
  }
  .title {
    color: #818fa9;
    font-size: 14px;
    margin: 0 0 3px 13px;
  }

  .label-text {
    color: #cdd2dd;
    font-size: 16px;
    line-height: 16px;
    margin: 0 12px;
    text-transform: capitalize;
  }

  /* label ellipsis when selected */
  .label-text.selected {
    display: inline-block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: calc(100%);
    height: 1.2em;
  }

  .selected--container .arrow {
    margin-right: 10px;
  }

  .selected--container .arrow.up svg {
    transform: rotate(180deg);
  }

  /* dropdown menu */
  .dropdown-menu--container {
    width: 100%;
    margin-top: 0px;
    cursor: pointer;
    overflow-y: auto;
    overflow-x: hidden;
    border: 1px solid #2b3240;
    box-sizing: border-box;
    background: #212631;
    opacity: 0;
    transition: all 0.3s ease;
    position: absolute; // need to be placed above possible elements below
    z-index: 9999;
    display: none;
  }
  .dropdown-menu--container.opened {
    opacity: 1;
  }
  .dropdown-menu--container .option--container {
    border-bottom: 1px solid #2b3240;
    transition: all 0.3s ease-in-out;
    width: 100%;
    height: 35px;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  .dropdown-menu--container .selected--option--container {
    border-bottom: 1px solid #2b3240;
    transition: all 0.3s ease-in-out;
    width: 100%;
    height: 35px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    background-color:#171A21;
  }

  .dropdown-menu--container .option--container:last-child {
    border-bottom: none;
  }
  .dropdown-menu--container .option {
    display: flex;
    justify-content: flex-start;
  }
  .dropdown-menu--container .checkbox {
    margin-right: 10px;
  }

  /* hover states */
  .option--container:hover {
    background: #1c2029;
  }

`;

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
    this.selectedName = dropdownOptions[0].name; // defaults to the first option
    this.isDropdownOpened = false; // dropdown defaults to closed
    this.dropdownMenuEl = '';
  }

  render() {
    if (!this.dropdownMenuEl && this.renderRoot.querySelector('.dropdown-menu--container')) {
      this.dropdownMenuEl = this.renderRoot.querySelector('.dropdown-menu--container');
    }
    return this.template();
  }

  template() {
    if (this.record && this.record[FIELD_KEY]) {
      this.selectedName = this.record[FIELD_KEY].value;
    }

    if (!this.size) {
      this.size = this.record.size || SIZE;
    }

    const match = dropdownOptions.find(option => option.name === this.selectedName);
    const indicatorColor = match ? match.color : '#818FA9'; // if not found, fall back to grey

    return html`
      <div class="slim-widget--container">
        <div class="dropdown--container">
          <div class="selected--container ${this.size}" @click="${() => this.dropdownOpenStateHandler()}">
            <div class="selected">
              <div class="indicator" style="--indicatorColor:${indicatorColor}"></div>
              <div class="text-label-container">
              ${this.size === 'large'
                ? html`
                    <div class="title">${TITLE}</div>
                  `
                : html``}
              <div class="label-text selected">${this.selectedName}</div>
            </div>
            </div>
            <div class=${this.isDropdownOpened ? 'arrow up' : 'arrow'}>${arrowIcon}</div>
          </div>
        </div>
        <div class="${this.isDropdownOpened ? 'dropdown-menu--container opened' : 'dropdown-menu--container'}">
          ${repeat(dropdownOptions, option => option.name, option => this.optionTemplate(option))}
        </div>
      </div>
    `;
  }

  optionTemplate(option) {
    if (!option) return '';
    const isActive = option.name === this.selectedName;
    return html`
      <div class="${isActive ? 'selected--option--container' : 'option--container'}" @click="${() => this.selectHandler(option.name)}">
        <div class="option">
          <div class="indicator" style="--indicatorColor:${option.color}"></div>
          <div class="label-text">${option.name}</div>
        </div>
        ${isActive
          ? html`
              <div class="checkbox">${checkboxIcon}</div>
            `
          : ``}
      </div>
    `;
  }

  dropdownOpenStateHandler() {
    this.isDropdownOpened = !this.isDropdownOpened;
    if (this.isDropdownOpened) {
      this.dropdownMenuShow();
    } else {
      this.dropdownMenuHide();
    }
    this.requestUpdate();
  }

  selectHandler(selectedValue) {
    this.isDropdownOpened = false; // close dropdown menu
    this.dropdownMenuHide();
    this.selectedName = selectedValue;
    this.updateRecordValue(FIELD_KEY, this.selectedName);
    this.requestUpdate();
  }

  dropdownMenuHide() {
    setTimeout(() => {
      if (!this.isDropdownOpened) {
        this.dropdownMenuEl.style.display = 'none';
      }
    }, 100);
  }

  dropdownMenuShow() {
    this.dropdownMenuEl.style.display = 'block';
    this.dropdownMenuEl.style['z-index'] = '9999';
  }
}

