function checklistTemplate(checklistField, clickHandler, checkboxes) {
  return html`
    <li class="task-checklist--list-item">
      ${
        hasSubtasks(checklistField)
          ? html`
              <div class="sub-task-line ${lastSubtaskHasLabel(checklistField.subTasks) ? 'has-sublabel' : ''}"></div>
            `
          : ''
      }
      <div class="task-list--checkbox--container">
        <div class="task-list--checkbox-wrapper">
          <input
            type="checkbox"
            ?disabled=${isDisabled(checklistField, checkboxes)}
            .checked=${checkboxIsChecked(checklistField, checkboxes)}
            class="task-list--checkbox-input ${
              hasActiveSubtask(checklistField.subTasks, checkboxes) ? 'subtask-active' : ''
            }"
            @click=${() => clickHandler(checklistField)}
          >
          </input>
        </div>
        <div class="task-list--checkbox-label-container ${
          checkboxIsChecked(checklistField, checkboxes) ? 'active' : ''
        } ${hasSubtasks(checklistField) ? 'has-subtasks' : ''}">
          <div class="label-container">  
            <div class="checkbox-label-container--label" title="${checklistField.label}">${checklistField.label}</div>
          </div>
          <div class="checkbox-label-container--sublabel" title="${checklistField.subLabel}">${
    checklistField.subLabel
  }</div>
        </div>
      </div>
      ${checklistSubTemplate(checklistField.subTasks, clickHandler, checkboxes)}
    </li>

  `;
}

function isDisabled(checklistField, checkboxes) {
  return checkboxIsChecked(checklistField, checkboxes) || hasActiveSubtask(checklistField.subTasks, checkboxes);
}

function hasSubtasks(checklistField) {
  return checklistField.subTasks && checklistField.subTasks.length;
}

function lastSubtaskHasLabel(subtasks = []) {
  if (!subtasks.length) return false;

  return !!subtasks[subtasks.length - 1].subLabel;
}

function hasActiveSubtask(subtasks = [], checkboxes) {
  if (!subtasks.length) return false;

  return subtasks.some(ch => !checkboxIsChecked(ch, checkboxes));
}

function checkboxIsChecked(checkbox, checkboxes) {
  if (!(checkbox.checkboxKey && Array.isArray(checkboxes) && checkboxes.length)) return false;

  return (
    checkboxes.some(c => c.value === checkbox.checkboxKey) ||
    (checkbox.subTasks && checkbox.subTasks.length && !hasActiveSubtask(checkbox.subTasks, checkboxes))
  );
}

function checklistSubTemplate(subTasks, clickHandler, checkboxes) {
  if (!(Array.isArray(subTasks) && subTasks.length)) return;

  return html`
    <ul class="task-checklist--list sub-list">
      ${subTasks.map((task, i) => checklistTemplate(task, clickHandler, checkboxes))}
    </ul>
  `;
}

function checkParent(parent, subCheckboxKey, checkboxes = []) {
  if (!(parent && parent.subTasks && parent.subTasks.length && subCheckboxKey)) return false;

  // check that every other checkbox other than the one to be checked is already checked
  const subs = parent.subTasks.filter(sub => sub.checkboxKey !== subCheckboxKey);
  return !subs.length || subs.every(s => checkboxIsChecked(s, checkboxes));
}

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  clickHandler = (checklistField, disabled = false) => {
    if (disabled) {
      return;
    }

    try {
      const checkboxes = this.record[checkboxesFieldKey] ? this.record[checkboxesFieldKey].map(c => c.value) : [];
      if (checklistField.taskId) {
        this.triggerIntegration(checklistField.taskId);
      }

      if (this.isValidField(checklistField.fieldKey) && checklistField.fieldKey && checklistField.value) {
        // update field
        this.updateRecordValue(checklistField.fieldKey, checklistField.value);
      }

      const updates = [checklistField.checkboxKey];

      // check if parent should be checked, eg all subtasks are checked
      const parent = checklistFields.find(
        ch => ch.subTasks && ch.subTasks.some(s => s.checkboxKey === checklistField.checkboxKey)
      );

      if (checkParent(parent, checklistField.checkboxKey, this.record[checkboxesFieldKey])) {
        updates.push(parent.checkboxKey);
        if (this.isValidField(parent.fieldKey) && parent.fieldKey && parent.value) {
          // update parent field
          this.updateRecordValue(parent.fieldKey, parent.value);
        }
      }

      this.updateRecordValue(checkboxesFieldKey, [...checkboxes, ...updates]);
    } catch {
      console.log('Failed to execute task with id', checklistField.taskId);
    }
  };

  constructor() {
    super();
  }

  isValidField(key) {
    // true if field exists in the app
    return this.contextData.application.fields.some(field => field.key === key);
  }

  render() {
    if (!(this.record && Array.isArray(checklistFields) && checklistFields.length)) return html``;

    return html`
      <ul class="task-checklist--list top">
        <div class="task-checklist--header">${widgetHeader}</div>
        ${checklistFields.map((field, i) =>
          checklistTemplate(field, this.clickHandler, this.record[checkboxesFieldKey])
        )}
      </ul>
    `;
  }
}

