  async mainTemplate(buttonsList) {
    currentUser = await this.getUser()


    return html`
      <div class="action-buttons--container">
        ${repeat(buttonsList, button => button.name, button => this.actionButtonTemplate(button))}
      </div>
    `;
  }

  actionButtonTemplate(button) {
    if (!button) return html``;

    return html`
      <div class="action-button--container">
        <div
          class="${button.isActive ? 'action-button active' : 'action-button'}"
          @click="${() => this.clickHandler(button)}"
        >
          <div class="icon--container">
            ${button.svg}
          </div>

          <div class="action--label">${button.displayName || button.name}</div>
        </div>
      </div>
    `;
  }

  clickHandler(button) {
    button.isActive = true;
    this.updateTimeTracking("Time Tracking Paused");
    this.pauseTimeTracking();
    this.requestUpdate();
  }

  updateTimeTracking(newTimeTrackingStatus) {
    this.updateLastTimeTracking();

    var newTimeTrackingStatusTimestamp = new Date();
    newTimeTrackingStatusTimestamp = newTimeTrackingStatusTimestamp.toISOString();

    this.updateRecordValue('time-tracking-status', newTimeTrackingStatus);
    this.updateRecordValue('time-tracking-status-timestamp', newTimeTrackingStatusTimestamp);
    this.updateRecordValue('time-tracking-user', {'name':this.contextData.currentUser.displayName, 'id':this.contextData.currentUser.id, '$type': "Core.Models.Utilities.UserGroupSelection, Core"});
  }

  updateLastTimeTracking() {
    const timeTrackingStatus = this.record['time-tracking-status'].value;
    const timeTrackingStatusTimestamp = this.record['time-tracking-status-timestamp'] || this.record['case_created-on'];;
    const currentUser = this.record['time-tracking-user'];
    const minutesInStatus = this.calculateMinutesInStatus();

    this.updateRecordValue('minutes-in-last-status', minutesInStatus);
    this.updateRecordValue('last-time-tracking-status', timeTrackingStatus);
    this.updateRecordValue('last-time-tracking-status-timestamp', timeTrackingStatusTimestamp);
    this.updateRecordValue('last-time-tracking-user', currentUser);
  }

  calculateMinutesInStatus() {
    var currentDate = new Date();
    var timestamp = new Date(this.record['time-tracking-status-timestamp']);
    var minutesInStatus = Math.floor(((currentDate - timestamp) || 0) / 600) / 100;
    return minutesInStatus;
  }
  // specific to "Claim Case" below
  pauseTimeTracking() {
    if (this.record){
      // Save record
      this.triggerSave()
    }
  }

  deduplicate(ob) {
    var o = 
      ob.filter((thing, index) => {
          const _thing = JSON.stringify(thing);
          return index === ob.findIndex(obj => {
            return JSON.stringify(obj) === _thing;
          });
      });
    return(o);
  }

  getUser() {
    var uid = this.contextData.currentUser.id;
    if (!this.contextData) {
      return Promise.resolve(null);
    }
    return fetch(`${this.contextData.origin}/api/user/${uid}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .catch(error => console.error('Error:', error));

  }
}

