export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
    actionButtons.forEach(button => (button.isActive = false)); // reset active states
  }

  render() {
    return html`
      ${resolvePromise(this.mainTemplate(actionButtons))}
    `;
  }

  async mainTemplate(buttonsList) {
    // For Escalate Button
    groups = await this.getGroups().then(res=>res.items); // getting a list of groups
    group = await this.getGroup(); // Gets current User
    //

    return html`
      <div class="action-buttons--container">
        ${repeat(buttonsList, button => button.name, button => this.actionButtonTemplate(button))}
      </div>
    `;
  }

  actionButtonTemplate(button) {
    if (!button) return html``;

    return html`
      <div class="action-button--container">
        <div
          class="${button.isActive ? 'action-button active' : 'action-button'}"
          @click="${() => this.clickHandler(button)}"
        >
          <div class="icon--container">
            ${button.svg}
          </div>

          <div class="action--label">${button.displayName || button.name}</div>
        </div>
      </div>
    `;
  }


  //Escalate-specific code below

  clickHandler(button) {
    button.isActive = true;
    this.updateTimeTracking("Case Escalated");
    this.escalateCase();
    this.requestUpdate();
  }

  updateTimeTracking(newTimeTrackingStatus) {
    this.updateLastTimeTracking();

    var newTimeTrackingStatusTimestamp = new Date();
    newTimeTrackingStatusTimestamp = newTimeTrackingStatusTimestamp.toISOString();

    this.updateRecordValue('time-tracking-status', newTimeTrackingStatus);
    this.updateRecordValue('time-tracking-status-timestamp', newTimeTrackingStatusTimestamp);
    this.updateRecordValue('time-tracking-user', {'name':this.contextData.currentUser.displayName, 'id':this.contextData.currentUser.id, '$type': "Core.Models.Utilities.UserGroupSelection, Core"});
  }

  updateLastTimeTracking() {
    const timeTrackingStatus = this.record['time-tracking-status'].value;
    const timeTrackingStatusTimestamp = this.record['time-tracking-status-timestamp'] || this.record['case_created-on'];;
    const currentUser = this.record['time-tracking-user'];
    const minutesInStatus = this.calculateMinutesInStatus();

    this.updateRecordValue('minutes-in-last-status', minutesInStatus);
    this.updateRecordValue('last-time-tracking-status', timeTrackingStatus);
    this.updateRecordValue('last-time-tracking-status-timestamp', timeTrackingStatusTimestamp);
    this.updateRecordValue('last-time-tracking-user', currentUser);
  }

  calculateMinutesInStatus() {
    var currentDate = new Date();
    var timestamp = new Date(this.record['time-tracking-status-timestamp']);
    var minutesInStatus = Math.floor(((currentDate - timestamp) || 0) / 600) / 100;
    return minutesInStatus;
  }

  escalateCase() {
    if (this.record){
      var primary_role_name = group.primaryGroup.name;
      var primary_role_id = group.primaryGroup.id;
      var user_name = this.contextData.currentUser.displayName;
      var user_id = this.contextData.currentUser.id

      // Define escalation Tier
      var escalation_tier = null;
      if (primary_role_name == 'TIER-1') {
        escalation_tier = 'TIER-2';
      } else if (primary_role_name == 'TIER-2') {
        escalation_tier = 'TIER-3';
      } else if (primary_role_name == 'TIER-3') {
        escalation_tier = 'TIER-3';
      } else {
        escalation_tier = 'TIER-1';
      }

      // Get Groups from Swimlane
      // above in mainTempate()

      // Match Escalation Tier to Group
      var group_array = groups;
      var escalation_group = null;
      // console.log(group_array)
      for (var i=0; i<group_array.length; i++){
        if (escalation_tier == group_array[i]['name']) {
          escalation_group = {
            'name': group_array[i]['name'],
            'id': group_array[i]['id'],
            '$type': "Core.Models.Utilities.UserGroupSelection, Core"
          }
        }
      }

      // console.log(escalation_group)

      // Update Case Escalated To
      if (escalation_group) {
        this.updateRecordValue('case_escalated-to', escalation_group);
      }


      // Update Case Current Owner
      this.updateRecordValue('case_current-owner', null);

      //Update Case Current Tier
      this.updateRecordValue('case_current-tier', null);

      // Update Case Owners
      var case_owners = [];
      if (this.record['case_owners'] && this.record['case_owners'].length > 0) {
        for (var i=0; i<this.record['case_owners'].length; i++) {
          var temp_dict = {'name': this.record['case_owners'][i]['name'], 'id': this.record['case_owners'][i]['id']};
          temp_dict['$type'] = "Core.Models.Utilities.UserGroupSelection, Core";
          case_owners.push(temp_dict);
        }
      }
      case_owners.push({'name':user_name, 'id':user_id, '$type': "Core.Models.Utilities.UserGroupSelection, Core"});
      var case_owners_unique = this.deduplicate(case_owners);
      this.updateRecordValue('case_owners', case_owners_unique)

      // Update Case Worked Tiers
      var case_worked_tiers = [];
      if (this.record['case_worked-tiers'] && this.record['case_worked-tiers'].length > 0) {
        for (var i=0; i<this.record['case_worked-tiers'].length; i++) {
          var temp_dict = {'name': this.record['case_worked-tiers'][i]['name'], 'id': this.record['case_worked-tiers'][i]['id']};
          temp_dict['$type'] = "Core.Models.Utilities.UserGroupSelection, Core";
          case_worked_tiers.push(temp_dict);
        }
      }
      case_worked_tiers.push({'name':primary_role_name, 'id':primary_role_id, '$type': "Core.Models.Utilities.UserGroupSelection, Core"});
      var case_worked_tiers_unique = this.deduplicate(case_worked_tiers);
      this.updateRecordValue('case_worked-tiers', case_worked_tiers_unique)

      // Update Case Escalated On
      var case_escalated_on = new Date();
      case_escalated_on = case_escalated_on.toISOString();
      this.updateRecordValue('case_escalated-on', case_escalated_on);

      // Update Case Status
      var case_status = 'Requires Attention'
      this.updateRecordValue('case_status', case_status);

      // Save record
      this.triggerSave()
    }
  }

  deduplicate(ob) {
    var o = 
      ob.filter((thing, index) => {
          const _thing = JSON.stringify(thing);
          return index === ob.findIndex(obj => {
            return JSON.stringify(obj) === _thing;
          });
      });
    return(o);
  }

  getGroups() {
    if (!this.contextData) {
      return Promise.resolve(null);
    }
    return Promise.resolve(fetch(`${this.contextData.origin}/api/groups`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .catch(error => console.error('Error:', error))
    );
  }

  getGroup() {

    if (!this.contextData) {
      return Promise.resolve(null);
    }
    var uid = this.contextData.currentUser.id;
    return fetch(`${this.contextData.origin}/api/user/${uid}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .catch(error => console.error('Error:', error));

  }


}

