    if (!this.demo) {
      const id = this.record[FIELD_KEY] ? this.record[FIELD_KEY].id : undefined;
      this.assignee = await this.fetch(id);
    }
    if (this.record[value]) {
      this.theme = this.record[value];
    }
    if (this.assignee) {
      return html`
        <div class="${this.theme === 'default' ? 'assignee' : 'assignee-dark'}">
          <div class="assignee-avatar">
            ${this.assignee.avatar
              ? html`
                  <img class="avatar" src="${this.assignee.avatar}" />
                `
              : html`
                  <span>
                    ${this.assignee.displayName
                      .split(' ')
                      .map(n => n[0])
                      .join('')}</span
                  >
                `}
          </div>
          <div class="assignee-details">
            <p class=${this.theme === 'default' ? 'assignee-title' : 'assignee-dark-title'}>Assignee</p>
            <span class="assignee-name">${this.assignee.displayName ? this.assignee.displayName : 'Not Assigned'}</span>
            <p id="roles">${this.assignee.roles.map(r => r.name).join(', ')}</p>
          </div>
        </div>
      `;
    }

    return html`
      <div class="${this.theme === 'default' ? 'assignee' : 'assignee-dark'}">
        <div class="no-assignee"></div>
        <div class="assignee-details">
          <p class=${this.theme === 'default' ? 'assignee-title' : 'assignee-dark-title'}>Assignee</p>
          <span class="assignee-name">Not Assigned</span>
        </div>
      </div>
    `;
  }

  fetch(id) {
    if (!this.contextData || !id) {
      return Promise.resolve(null);
    }
    return fetch(`${this.contextData.origin}/api/user/${id}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`
      }
    })
      .then(res => res.json())
      .catch(error => console.error('Error:', error));
  }
}

/**
 * usage-progress v2.0.0
 * https://swimlane.github.io/custom-widgets/usage-progress/
 *
 */

import { SwimlaneElement, html, css } from '@swimlane/swimlane-element@1';

const circleWidth = 4;
const progressWidth = 8;
const overTimeWidth = 12;

const FIELD1 = 'sla_hours';
const FIELD2 = 'sla_minutes';
const FIELD3 = 'case-reopen-time';
const FIELD4 = 'time-tracking-status';
const FIELD5 = 'minutes-in-current-status';

const COLOR = 'light'; // color 'light' or 'dark'

function formatTime(usage) {
  if (!usage) {
    return '00:00';
  }
  let mins = 0;
  let hours = 0;
  if (usage >= 60) {
    hours = Math.floor(usage / 60);
    mins = usage % 60;
    mins = mins < 10 ? `0${mins}` : mins;
    hours = hours < 10 ? `0${hours}` : hours;
  } else {
    mins = usage < 10 ? `0${usage}` : usage;
    hours = '00';
  }
  return `${hours}:${mins}`;
}
const template = (percentage, totalHours, minutes, totalHoursToMinutes, reopenTime, timeTrackingStatus, color) => {
  percentage = percentage ? percentage : 0;
  let progressColor;
  if (percentage == 0) {
    progressColor = '#FFFFFF';
  } else if (percentage > 0 && percentage <= 25) {
    progressColor = '#CEF9F0';
  } else if (percentage > 25 && percentage <= 50) {
    progressColor = '#A1F3E2';
  } else if (percentage > 50 && percentage <= 75) {
    progressColor = '#74EDD4';
  } else if (percentage > 75 && percentage < 100) {
    progressColor = '#C4D082';
  } else {
    progressColor = '#FFBB47';
  }

  const circle = (percentage / 100) * 100 * Math.PI + ',9999';
  let totalHoursText;

  if (totalHours > 1) {
    totalHoursText = `of ${totalHours} Hours`;
  } else if (totalHours == 1) {
    totalHoursText = `of ${totalHours} Hour`;
  } else if (!totalHours) {
    totalHoursText = `of 0 Hours`;
  }

  let formatedPercentage = ((minutes / totalHoursToMinutes) * 100).toFixed(2);
  let percentageText = isNaN(formatedPercentage) || formatedPercentage == Infinity ? '0.00%' : `${formatedPercentage}%`;

  // overtime
  let overTimeStroke;
  let overTimeStrokeDash;
  if (minutes > totalHoursToMinutes) {
    let m = minutes % 60;
    if (m === 0 && minutes !== 0) {
      m = 60;
    }
    let p = Math.round((m / 60) * 100);
    overTimeStroke = '#FFBB47';
    overTimeStrokeDash = (p / 100) * 100 * Math.PI + ',9999';
  } else {
    overTimeStroke = 'none';
  }
  let circleColor = color === 'light' ? '#7AB9FF' : '#5A6884';

  let reopenDiv = false;
  if (reopenTime){
    let now = new Date();
    let reopened = new Date(reopenTime);
    let reopenMinAgo = Math.round((now - reopened) / 60 / 1000);
    reopenDiv = html`<div class="reopened">Case Reopened ${formatTime(reopenMinAgo)} Ago </div>`;
  } 

  let pausedDiv = false;
  if (timeTrackingStatus == "Time Tracking Paused") {
    pausedDiv = html`<div class="reopened">Time Tracking Paused</div>`;
  }

  return html`
    <div class="usage-progress-circle ${color}">
      <div class="progress-circle-container">
        <svg
          class="hours"
          viewBox="0 0 106 106"
          version="1.1"
          xmlns="http://www.w3.org/2000/svg"
          xmlns:xlink="http://www.w3.org/1999/xlink"
        >
          <g class="Page-1" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
            <g class="ProgressBar" transform="translate(-17.000000, -17.000000)">
              <defs>
                <filter id="shadow">
                  <feDropShadow dx="0" dy="0" stdDeviation="4" flood-color="rgba(59, 68, 87, 0.75)" />
                </filter>
              </defs>
              <circle
                class="Oval"
                stroke="${circleColor}"
                stroke-width="${circleWidth}"
                fill-rule="nonzero"
                cx="70"
                cy="70"
                r="50"
              ></circle>
              <path
                class="path"
                d="M70,120 C97.6142375,120 120,97.6142375 120,70 C120,42.3857625 97.6142375,20 70,20 C42.3857625,20 20,42.3857625 20,70 C20,97.6142375 42.3857625,120 70,120 Z"
                class="Oval-Copy"
                stroke="${progressColor}"
                stroke-width="${progressWidth}"
                stroke-dasharray="${circle}"
                fill-rule="nonzero"
                transform="translate(70.000000, 70.000000) rotate(-125.000000) translate(-70.000000, -70.000000)"
                stroke-linecap="round"
                style="filter:url(#shadow)"
              ></path>
              <path
                class="path-over"
                d="M70,120 C97.6142375,120 120,97.6142375 120,70 C120,42.3857625 97.6142375,20 70,20 C42.3857625,20 20,42.3857625 20,70 C20,97.6142375 42.3857625,120 70,120 Z"
                stroke="${overTimeStroke}"
                stroke-dasharray="${overTimeStrokeDash}"
                class="Oval-Copy"
                stroke-width="calc(${overTimeWidth})"
                fill-rule="nonzero"
                transform="translate(70.000000, 70.000000) rotate(-125.000000) translate(-70.000000, -70.000000) "
                stroke-linecap="round"
              ></path>
            </g>
          </g>
        </svg>
      </div>
      <div class="text-container">
        <div class="usage-header">SLA Usage</div>
        ${ reopenDiv ? reopenDiv : 
         pausedDiv ? pausedDiv : 
         html`<div class="usage-text">
          <div>${percentageText}&nbsp;&nbsp;/&nbsp;&nbsp;</div>
          <div>${formatTime(minutes)} ${totalHoursText}</div>
        </div>`
        }
      </div>
    </div>
  `;
};

