const resolvePromise = directive(promise => part => {
  Promise.resolve(promise).then(resolvedValue => {
    part.setValue(resolvedValue);
    part.commit();
  });
});

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
    this.demo = false;
    this.newDashoffset = 0;
  }

  resizedCallback(entry) {
    /** overwrite resize callback; no longer calls requestupdate */
  }

  render() {
    if (!this.record) return;

    if (this.record[DEMO_FIELD_KEY]) {
      this.demo = true;
      this.parseDemoJsonData();
    }

    this.newDashoffset = 0;

    return resolvePromise(this.template());
  }

  async template() {
    if (!this.demo && !this.correlationKeys && this.recordHasReferenceData()) {
      await this.constructKeyIdMap();
      await this.getReferenceData();
      this.getCorrelationKeys();
      if (this.correlationKeys) {
        this.sortByPrevalence();
      }
    }

    if (!this.correlationKeys) return html``;

    const totalIOC = this.correlationKeys.reduce((a, c) => {
      return a + c.amount;
    }, 0);

    return html`
      <div class="${orientation === 'horizontal' ? 'correlation-keys' : 'correlation-keys-vertical'}">
        <div class="${orientation === 'horizontal' ? 'correlation-keys-average' : 'correlation-keys-average-vertical'}">
          <svg width="106px" height="106px" viewBox="0 0 40 40">
            <circle cx="20" cy="20" r="15.91549430918954" fill="#3b44574d"></circle>
            <circle cx="20" cy="20" r="15.91549430918954" fill="transparent" stroke="#fff" stroke-width="3"></circle>
            ${repeat(
              this.correlationKeys,
              correlationKey => correlationKey.value,
              (correlationKey, index) => this.correlationKeyPieChartTemplate(correlationKey, index, totalIOC)
            )}
            <g>
              <text x="50%" y="50%" class="chart-number">
                ${totalIOC}
              </text>
              <text x="50%" y="50%" class="chart-label">
                Keys
              </text>
            </g>
          </svg>
        </div>
        <figcaption class="${orientation === 'horizontal' ? 'figure-key' : 'figure-key-vertical'}">
          <p class="sr-only">Correlation Keys Across Source Alerts</p>
          <ul
            class="${orientation === 'horizontal' ? 'figure-key-list' : 'figure-key-list-vertical'}"
            aria-hidden="true"
            role="presentation"
          >
            ${repeat(
              this.correlationKeys,
              correlationKey => correlationKey.value,
              (correlationKey, index) => this.correlationKeyTemplate(correlationKey, index)
            )}
          </ul>
        </figcaption>
      </div>
    `;
  }

  correlationKeyPieChartTemplate(correlationKey, index, totalIOC) {
    if (!correlationKey) return html``;

    const dashArray = (correlationKey.amount * 100) / totalIOC;
    const dashArrayLeft = 100 - dashArray;
    this.newDashoffset += dashArray;
    const dashoffset = 100 - (this.newDashoffset - dashArray) + initialStrokeDashoffset;

    return svg`
      <circle
        cx="20"
        cy="20"
        r="15.91549430918954" 
        fill="transparent"
        stroke="${
          index > colors.length - 1 ? colors[Math.floor(Math.random() * colors.length)].bgColor : colors[index].bgColor
        }" 
        stroke-width="3"
        stroke-dasharray="${dashArray.toFixed(2)} ${dashArrayLeft.toFixed(2)}"
        stroke-dashoffset="${index === 0 ? initialStrokeDashoffset : dashoffset.toFixed(2)}"
      ></circle>
    `;
  }

  correlationKeyTemplate(correlationKey, index) {
    if (!correlationKey) return html``;

    const colorItem = index > colors.length - 1 ? colors[Math.floor(Math.random() * colors.length)] : colors[index];

    return html`
      <li>
        <label class="ioc-label ${orientation === 'vertical' ? 'vertical' : ''}">${correlationKey.value}</label>
        <span class="shape-circle" style="background-color:${colorItem.bgColor}; color:${colorItem.color};"
          >${correlationKey.amount}x</span
        >
      </li>
    `;
  }

  parseDemoJsonData() {
    try {
      this.correlationKeys =
        typeof this.record[DEMO_FIELD_KEY] === 'string'
          ? JSON.parse(this.record[DEMO_FIELD_KEY])
          : this.record[DEMO_FIELD_KEY];
    } catch (e) {
      console.log(e);
    }
    if (this.correlationKeys) {
      this.correlationKeys = this.correlationKeys[DEMO_MAIN_KEY];
      this.sortByPrevalence();
    }
  }

  async getReferenceData() {
    this.refData = new Array();

    await Promise.all(
      REF_FIELDS.map(async r => {
        if (this.record[r.reference_field]) {
          const referenceField = this.contextData.application.fields.find(f => f.key === r.reference_field);
          this.addFieldToColumns(referenceField, r.correlation_keys_field);
          const data = await getReferenceData(
            referenceField,
            this.record['tracking-id'],
            this.record[r.reference_field],
            this.contextData.token,
            location.origin
          );

          this.refData.push(...data);
        }
      })
    );
  }

  addFieldToColumns(referenceField, correlationKeysFieldName) {
    referenceField.columns.push(this.fieldIds[referenceField.key + correlationKeysFieldName]);
  }

  async constructKeyIdMap() {
    this.fieldIds = new Object();

    await Promise.all(
      REF_FIELDS.map(async r => {
        const referenceField = this.contextData.application.fields.find(f => f.key === r.reference_field);
        const refAppId = referenceField.targetId;
        const app = await this.getApp(refAppId);
        const fields = [r.correlation_keys_field];

        app.fields.forEach(field => {
          if (fields.includes(field.key)) {
            this.fieldIds[r.reference_field + field.key] = field.id;
          }
        });
      })
    );
  }

  async getApp(appId) {
    return fetch(`${location.origin}/api/app/${appId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  getCorrelationKeys() {
    if (!this.refData || !this.refData.length) return;

    this.correlationKeysRaw = new Array();

    REF_FIELDS.map(r => {
      if (this.record[r.reference_field]) {
        const correlationKeys = this.fieldIds[r.reference_field + r.correlation_keys_field];
        const data = this.refData
          .map(d => {
            return d.values[correlationKeys].map(k => {
              return k.value;
            });
          })
          .flat(1);

        this.correlationKeysRaw.push(...data);
      }
    });

    this.correlationKeys = [];
    const map = new Map();

    for (const item of this.correlationKeysRaw) {
      if (!map.has(item)) {
        map.set(item, true);
        this.correlationKeys.push({
          value: item,
          amount: this.countOccurrences(item)
        });
      }
    }
  }

  countOccurrences(value) {
    return this.correlationKeysRaw.reduce((o, v) => (v === value ? o + 1 : o), 0);
  }

  recordHasReferenceData() {
    let hasReferenceData = false;

    for (var i = 0; i < REF_FIELDS.length; i++) {
      if (this.record[REF_FIELDS[i].reference_field]) {
        hasReferenceData = true;
        break;
      }
    }

    return hasReferenceData;
  }

  sortByPrevalence() {
    this.correlationKeys.sort((a, b) => b.amount - a.amount);
  }
}

