const resolvePromise = directive(promise => part => {
  Promise.resolve(promise).then(resolvedValue => {
    part.setValue(resolvedValue);
    part.commit();
  });
});

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
    this.demo = false;
    this.isDropdownOpened = false;
    this.searchText = '';
    this.selectedRecordNumber = '';
    this.selectedAlert = '';
    this.isPhishingOrImage = 'image';
    this.refAppId = '';
    this.alertsType = '';
    this.apps = {};
    this.fieldIds = {};
  }

  resizedCallback(entry) {
    /** overwrite resize callback; no longer calls requestupdate */
  }

  render() {
    if (this.record[DEMO_FIELD_KEY] && !this.alerts) {
      this.demo = true;
      // this.alertsType = this.record[DEMO_ALERTS_TYPE_KEY];
      this.parseDemoJsonData();
      this.sortByDescDate(this.alerts);

    }
    return resolvePromise(this.template());
  }

  async template() {
    if (!this.record) return;
    if (!this.demo && !this.alerts) {
      for (var app in APP_AND_FIELD_DEFINITIONS) {
        if (this.record[APP_AND_FIELD_DEFINITIONS[app]['Source Field']] && this.record[APP_AND_FIELD_DEFINITIONS[app]['Source Field']].length) {
          await this.constructKeyIdMap(APP_AND_FIELD_DEFINITIONS[app])
          await this.getReferenceData(APP_AND_FIELD_DEFINITIONS[app])
          await this.getAppData(APP_AND_FIELD_DEFINITIONS[app])
          this.sortByDescDate(this.alerts)
        }
      }
    }

    if (!this.alerts) { // Covers Manual Cases
      this.alerts = [];
    }

    let alert = this.alerts ? this.alerts.find(alert => alert.recordNumber === this.selectedRecordNumber) : undefined;
    if (!alert) {
      if (this.alerts.length > 0) {
        alert = this.alerts[0];
      }
    }

    if (this.alerts.length > 0) {
      this.exportCsv(this.alerts);

      return html`
      <div class="widget-container">
        <div class="dropdown-header">Source Alerts</div>

        <div class="dropdown-container">
          <div class="selected-alert-container" @click="${() => this.dropdownStateHandler()}">
            <div class="alert-text selected">${this.selectedAlert ? this.selectedAlert : searchPlaceholder}</div>
            <div class="${this.isDropdownOpened ? 'arrow up' : 'arrow'}">${arrowIcon}</div>
          </div>
          <div class="dropdown-menu-container ${this.isDropdownOpened ? 'opened' : ''}">
            <div class="dropdown-search">
              <div class="search-icon">${searchIcon}</div>
              <input
                id="searchfield"
                type="text"
                .value="${this.searchText}"
                placeholder="${innerSearchPlaceholder}"
                @input="${e => this.searchInputHandler(e)}"
              />
              <div
                class="clear-icon ${this.searchText === '' ? 'disabled' : ''}"
                @click="${() => this.clearSelectionHandler()}"
              >
                ${clearIcon}
              </div>
            </div>
            <div class="option-list">
              ${this.alerts
                ? repeat(this.alerts, alert => alert.recordNumber, alert => this.optionTemplate(alert))
                : ''}
            </div>
          </div>
        </div>

        <div class="tabs-container">
          <div class="tabs-title">Recent Source Alerts</div>
          ${this.alerts
            ? repeat(this.alerts.slice(0, maxTabNumber), alert => alert.recordNumber, alert => this.tabTemplate(alert))
            : ''}
        </div>
        ${alert
        ? html`${this.alertDetailsTemplate(alert)}`
        : html``
      }
      </div>
    `;
    } else {
      return html`
      <div class="widget-container">
        <div class="dropdown-header">Source Alerts</div>

        <div class="dropdown-container">
          <div class="selected-alert-container" @click="${() => this.dropdownStateHandler()}">
            <div class="alert-text selected">${this.selectedAlert ? this.selectedAlert : searchPlaceholder}</div>
            <div class="${this.isDropdownOpened ? 'arrow up' : 'arrow'}">${arrowIcon}</div>
          </div>
          <div class="dropdown-menu-container ${this.isDropdownOpened ? 'opened' : ''}">
            <div class="dropdown-search">
              <div class="search-icon">${searchIcon}</div>
              <input
                id="searchfield"
                type="text"
                .value="${this.searchText}"
                placeholder="${innerSearchPlaceholder}"
                @input="${e => this.searchInputHandler(e)}"
              />
              <div
                class="clear-icon ${this.searchText === '' ? 'disabled' : ''}"
                @click="${() => this.clearSelectionHandler()}"
              >
                ${clearIcon}
              </div>
            </div>
            <div class="option-list">
              ${this.alerts
                ? repeat(this.alerts, alert => alert.recordNumber, alert => this.optionTemplate(alert))
                : ''}
            </div>
          </div>
        </div>

        <div class="tabs-container">
          <div class="tabs-title">Recent Source Alerts</div>
          ${this.alerts
            ? repeat(this.alerts.slice(0, maxTabNumber), alert => alert.recordNumber, alert => this.tabTemplate(alert))
            : ''}
        </div>
        <h2>No Source Alerts have been referenced.</h2>
      </div>

      `;
    }
  }

  optionTemplate(option) {
    if (!option) return '';

    const optionText = option.recordNumber + ': ' + option.subject;

    if (this.searchText && !optionText.toLowerCase().includes(this.searchText.toLowerCase())) return '';

    const isActive = option.recordNumber === this.selectedRecordNumber;

    return html`
      <div class="option-container" @click="${() => this.selectHandler(option)}">
        <div class="alert-text">${optionText}</div>
      </div>
    `;
  }

  tabTemplate(alert) {
    if (!alert) return '';

    const isActive = alert.recordNumber === this.selectedRecordNumber;

    return html`
      <div class="alert-tab ${isActive ? 'active' : ''}" @click="${() => this.tabClickHandler(alert)}">
        ${alert.recordNumber}
      </div>
    `;
  }

  dropdownStateHandler() {
    this.isDropdownOpened = !this.isDropdownOpened;
    this.focusSearchField();
    this.requestUpdate();
  }

  selectHandler(selectedOption) {
    this.isDropdownOpened = false;
    this.selectedAlert = selectedOption.recordNumber + ': ' + selectedOption.subject;
    this.selectedRecordNumber = selectedOption.recordNumber;
    this.moveAlertToTop(selectedOption);
    this.requestUpdate();
  }

  searchInputHandler(event) {
    // input's current value
    this.searchText = event.path[0].value;
    this.requestUpdate();
  }

  clearSelectionHandler() {
    this.searchText = '';
    this.focusSearchField();
    this.requestUpdate();
  }

  focusSearchField() {
    setTimeout(() => {
      this.shadowRoot.getElementById('searchfield').focus();
    }, 1);
  }

  navClickHandler(alert, selectedType, activeImage=false){
    alert.selectedType = selectedType;
    if (activeImage) {
      alert.activeImage = activeImage;
    }
    this.requestUpdate();
  }

  tabClickHandler(alert) {
    this.selectedAlert = alert.recordNumber + ': ' + alert.subject;
    this.selectedRecordNumber = alert.recordNumber;
    this.moveAlertToTop(alert);
    this.requestUpdate();
  }

  moveAlertToTop(alert) {
    this.alerts = this.alerts.filter(obj => obj.recordNumber !== alert.recordNumber);
    this.alerts.unshift(alert);
  }

  sortByDescDate(array) {
    if (!array) return;
    array.sort((a, b) => new Date(b.creationDate).getTime() - new Date(a.creationDate).getTime());
  }

  parseDemoJsonData() {
    try {
      this.alerts =
        typeof this.record[DEMO_FIELD_KEY] === 'string'
          ? JSON.parse(this.record[DEMO_FIELD_KEY])
          : this.record[DEMO_FIELD_KEY];
    } catch (e) {
      console.log(e);
    }

    if (this.alerts) {
      this.alerts = this.alerts[DEMO_MAIN_KEY];
    }
  }

  alertDetailsTemplate(alert) {
    if (!alert) {
      alert = this.alerts[0];
    }

    let imageNavHtml = html``;
    if (alert.images.length > 0) {
      for (let i in alert.images) {
        imageNavHtml = html`${imageNavHtml} <span class="${alert.activeImage == i && alert.selectedType == 'image' ? 'nav-active' : ''}" @click="${() => this.navClickHandler(alert, 'image', i)}">Image ${parseInt(i) + 1}</span>`;
      }
    } else {
      alert.selectedType = 'detail';
    }

    let detailNavHtml = html`<span class="${alert.selectedType == 'detail' ? 'nav-active' : ''}" @click="${() => this.navClickHandler(alert, 'detail')} ">Alert Details</span>`;

    let allDetailsNavHtml = html`
    <div class="details-nav">
    ${detailNavHtml}
    ${imageNavHtml}
    </div>
    `;

    let imageDisplayHtml = html`
    ${
      alert.selectedType == 'image'
      ? html`<div class="image-actions">
      <a class="download-image" href=${alert.images[alert.activeImage]} download="${imageFileName}"
      >Download Image</a>
      </div>
      <div class="alert-image"><img src="${alert.images[alert.activeImage]}" /></div>`
      : html``
    }
    `;

    let detailDisplayHtml = html`
    ${
      alert.selectedType == 'detail'
      ? html`<div class="image-actions">
      <a href="${this.encodedUri}" download="${fileName}"
      >Export Details</a>
      </div>
      <div class="details">${this.detailsTemplate(alert.fields)}</div>`
      : html``
    }
    `;

    return html`
      <div class="details-container">
        <div class="details-header">
            ${allDetailsNavHtml}
        </div>
        ${imageDisplayHtml}
        ${detailDisplayHtml}
      </div>
    `;

  }

  detailsTemplate(fields) {
    return fields.map(
      pd =>
        html`
        <div class="${
          pd.params['Full Width'] ? 'details-full-width' : ''} ${
          pd.params['Wrap Text'] ? 'details-wrap-text' : ''} ${
          (pd.params['Hide if Empty'] && (typeof(pd.value) === 'undefined' || pd.value.length == 0)) ? 'd-none' : ''} detail"><span class="details-title">${
          pd.key
          }</span><span class="details-description ${pd.params['Monospace'] ? 'monospace': ''}">${
          pd.value
        }<span/>
        </div>`
    );
  }

  exportCsv(alerts) {
    let rawKeys = alerts[0].fields.map(k => k.key)

    let csvContent = 'data:text/csv;charset=utf-8,';
    const alertKeys = rawKeys.sort();
    csvContent += alertKeys.join(',') + '\r\n'; // rows needs to be in the same order

    this.alerts.forEach(data => {
      // filter out data if key is missing from keys list
      const filtered = alertKeys.map(key => {
        let v = data.fields.filter(k => k.key === key)[0].value || '';

        v = v.toString();

        if (v instanceof Date) {
          v = v.toLocaleString();
        }

        v = v.replace(/"/g, '""');

        if (v.search(/("|,|\n)/g) >= 0) {
          v = '"' + v + '"';
        }

        return v;
      });

      csvContent += filtered.join(',') + '\r\n';
    });

    this.encodedUri = encodeURI(csvContent);
  }

  findVal(object, key) {
    var value;
    var self = this;
    Object.keys(object).some(function(k) {
        if (k === key) {
            value = object[k];
            return true;
        }
        if (object[k] && typeof object[k] === 'object') {
            value = self.findVal(object[k], key);
            return value !== undefined;
        }
    });
    if (typeof value === 'object') {
      if (value.hasOwnProperty('value')) {
        value = value.map(a => a.value).join(', ');
      } else if (value.hasOwnProperty('name')) {
        value = value.map(a => a.name).join(', ');
      } else {
        value = JSON.stringify(value)
      }
    }
    return value;
  }

  async getReferenceData(application) {
    const refField = application['Source Field']
    const referenceField = this.contextData.application.fields.find(f => f.key === refField);
    this.addFieldToColumns(application, referenceField);
    this.refData = await getReferenceData(
      referenceField,
      this.record.id,
      this.record[refField],
      this.contextData.token,
      location.origin
    );
  }

  addFieldToColumns(application, referenceField) {
    referenceField.columns.push(...Object.values(this.fieldIds[application['Display Name']]));
  }

  async constructKeyIdMap(application) {
    const referenceField = this.contextData.application.fields.find(f => f.key === application['Source Field']);
    this.refAppId = referenceField.targetId;
    const app = await this.getApp(this.refAppId);
    let appFields = {}
    for (var key in application['Summary Fields']) {
      appFields[key] = application['Summary Fields'][key];
    }
    for (var field in application['Display Fields']) {
      appFields[application['Display Fields'][field]['Display Name']] = application['Display Fields'][field]['Source Field']
    }
    for (var field in application['Image Fields']) {
      appFields[application['Image Fields'][field]['Display Name']] = application['Image Fields'][field]['Source Field']
    }
    appFields['Tracking ID'] = application['Tracking ID']

    const fields = Object.keys(appFields).map(function(key) {
      return appFields[key];
    });

    this.fieldIds[application['Display Name']] = app.fields.reduce((acc, field) => {
      if (fields.includes(field.key)) {
        acc[field.key] = field.id;
      }
      return acc;
    }, {});
  }

  async getApp(appId) {
    return fetch(`${location.origin}/api/app/${appId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  async getAppData(app) {
    const tracking_id = app['Tracking ID'];
    this.alerts = await Promise.all(
      this.refData.map(async r => {
        const fields = [];
        for (var field_def in app['Display Fields']) {
          const display_name = app['Display Fields'][field_def]['Display Name'];
          const raw_value = r.values[this.fieldIds[app['Display Name']][app['Display Fields'][field_def]['Source Field']]] || undefined;
          let value = undefined;
          if (typeof raw_value === 'object') {
            if ('value' in raw_value) {
              value = raw_value['value'];
            } else {
              value = this.findVal(raw_value, 'value');
              value = !value ? this.findVal(raw_value, 'name') : value;
            }
          } else {
            value = raw_value;
          }
          const params = {
            'Wrap Text': 'Wrap Text' in app['Display Fields'][field_def] ? app['Display Fields'][field_def]['Wrap Text'] : true, // Wrap text = true by default
            'Full Width': 'Full Width' in app['Display Fields'][field_def] ? app['Display Fields'][field_def]['Full Width'] : false, // default full width to false
            'Hide if Empty': 'Hide if Empty' in app['Display Fields'][field_def] ? app['Display Fields'][field_def]['Hide if Empty'] : false, // default hide to empty to false
            'Monospace': 'Monospace' in app['Display Fields'][field_def] ? app['Display Fields'][field_def]['Monospace'] : false,  // default monospace to false
          }
          fields.push({ key: display_name, value: value, params: params });
        }

        const has_images = 'Image Fields' in app && app['Image Fields'].length > 0 ? true : false;

        const imageBlobs = [];

        if (has_images) {
          for (var field_def in app['Image Fields']) {
            if (r.values[this.fieldIds[app['Display Name']][app['Image Fields'][field_def]['Source Field']]] && r.values[this.fieldIds[app['Display Name']][app['Image Fields'][field_def]['Source Field']]].length > 0) {
              for (var attachment in r.values[this.fieldIds[app['Display Name']][app['Image Fields'][field_def]['Source Field']]]) {
                imageBlobs.push(await this.getImage(r.values[this.fieldIds[app['Display Name']][app['Image Fields'][field_def]['Source Field']]][attachment].fileId));
              }
            }
          }
        }

        let subject = r.values[this.fieldIds[app['Display Name']][app['Summary Fields']['Subject']]];
        subject = typeof subject === 'object' ? subject.map(a => a.value).join(', ') : subject;

        let source = r.values[this.fieldIds[app['Display Name']][app['Summary Fields']['Source']]];
        source = typeof source === 'object' ? source.map(a => a.value).join(', ') : source;

        let destination = r.values[this.fieldIds[app['Display Name']][app['Summary Fields']['Destination']]];
        destination = typeof destination === 'object' ? destination.map(a => a.value).join(', ') : destination;

        return {
          recordNumber: r.values[this.fieldIds[app['Display Name']][app['Tracking ID']]],
          type: app['Display Name'],
          subject: subject,
          source: source,
          destination: destination,
          creationDate: r.values[this.fieldIds[app['Display Name']][app['Creation Date']]],
          fields: fields,
          images: imageBlobs,
          activeImage: 0,
          selectedType: has_images ? 'image' : 'detail',
          recordId: r.recordId
        };
      })
    );
  }

  async getImage(fileId) {
    return fetch(`${location.origin}/api/attachment/${fileId}`, {
      headers: {
        Authorization: `Bearer ${this.contextData.token}`
      }
    })
      .then(response => {
        return response.blob();
      })
      .then(blob => {
        const url = URL.createObjectURL(blob);
        return url;
      });
  }
}

