//Change key to Title case with speacial replace
function toTitleCase(str) {
    str = str.replace(/-/g, " ");
    return str.replace(
        /\w\S*/g,
        function (txt) {
            let word = txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            if (SpecialFieldNameRepace[word]) {
                word = SpecialFieldNameRepace[word]
            }
            return word;
        }
    );
}

export default class extends SwimlaneElement {
    static get styles() {
        return [super.styles, widgetCss];
    }

    formatPluginName(name) {
        if (!name) {
            return '';
        }

        const comparedValue = name[0].toUpperCase() + name.slice(1);

        if (comparedValue === 'Jira') {
            return 'Jira';
        } else if (comparedValue === 'ServiceNow') {
            return 'ServiceNow';
        } else {
            return '';
        }
    }

    render() {
        if (this.record && this.record[ServiceNowFields[0]]) {
            return this.template();
        }
    }

    template() {
        let html_out = [
            html`
            <div class="main-container">
              <div class="heading-content">ServiceNow Alerts</div>
            </div>
            <div class="horizontal-line"></div>
          `
        ];
        const match = ticketingPluginList.find(option => option.name === this.ticketingPlugin);
        console.log('match', match);

        let ServiceNowAvailableFields = [];

        for (let field of ServiceNowFields) {
            if (this.record[field]) {
                ServiceNowAvailableFields.push(field)
            }
        }

        for (var i = 0; i < ServiceNowAvailableFields.length; i += 1) {
          let field_name = toTitleCase(ServiceNowAvailableFields[i])
          let value = this.record[ServiceNowAvailableFields[i]]

          if (ServiceNowSubFieldMap[ServiceNowAvailableFields[i]]) {
              value = RankValues[this.record[ServiceNowAvailableFields[i]]['value']]
          }

          if (ServiceNowAvailableFields[i].includes("url")) {
              value = html`<a href="${this.record[ServiceNowAvailableFields[i]]}" target="_blank">${this.record[ServiceNowAvailableFields[i]]}</a>`
          }

          html_out.push(html`
              <div class="column">
                  <div class="title"> ${field_name}</div>
                  <div class="summary-content">
                      ${value}
                  </div>
              </div>
          `);

        }

        return html_out;
    }
}

