const isSvg = val => val && val.type === 'svg';

const resolvePromise = directive(promise => part => {
  Promise.resolve(promise).then(resolvedValue => {
    part.setValue(resolvedValue);
    part.commit();
  });
});

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
    this.demo = false;
    this.isDropdownOpened = false;
  }

  render() {
    if (this.record[DEMO_FIELD_KEY] && !this.analysisResults) {
      this.demo = true;
      this.parseDemoJsonData();
    }

    return resolvePromise(this.template());
  }

  updated() {
    // Queue data refresh in REFRESH_INTERVAL ms after last update
    if (REFRESH_INTERVAL > 0) {
      clearTimeout(this.timeoutId);
      this.timeoutId = setTimeout(async () => {
        await this.updateData();
        this.requestUpdate();
      }, REFRESH_INTERVAL);
    }
  }

  disconnectedCallback() {
    clearTimeout(this.timeoutId);
  }

  async getReferenceData() {
    const referenceField = this.contextData.application.fields.find(f => f.key === SOURCE_FIELD);
    this.refData = await getReferenceData(
      referenceField,
      this.record['tracking-id'],
      this.record[SOURCE_FIELD],
      this.contextData.token,
      location.origin
    );
  }

  async constructKeyIdMap() {
    const referenceField = this.contextData.application.fields.find(f => f.key === SOURCE_FIELD);
    const refAppId = referenceField.targetId;
    const app = await this.getApp(refAppId);
    this.referenceTrackingFieldId = app.trackingFieldId;
    this.fieldIds = app.fields.reduce((acc, field) => {
      if ([INDICATOR, TI_TOOL, TI_TAG, TI_SCORE, TI_PERMALINK].includes(field.key)) {
        acc[field.key] = field.id;
      }
      return acc;
    }, {});
  }

  async getApp(appId) {
    return fetch(`${location.origin}/api/app/${appId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  getAnalysisResultsData() {
    const indicatorId = this.fieldIds[INDICATOR];
    const tiToolId = this.fieldIds[TI_TOOL];
    const tiTagId = this.fieldIds[TI_TAG];
    const tiScoreId = this.fieldIds[TI_SCORE];
    const tiPermalink = this.fieldIds[TI_PERMALINK];

    this.analysisResults = this.refData.map(r => {
      return {
        trackingId: r.values[this.referenceTrackingFieldId],
        applicationId: r.applicationId,
        recordId: r.recordId,
        indicator:
          typeof r.values[indicatorId] === 'object'
            ? r.values[indicatorId].value
            : typeof r.values[indicatorId] !== 'undefined'
            ? r.values[indicatorId]
            : 'N/A',
        ti_tool:
          typeof r.values[tiToolId] === 'object'
            ? r.values[tiToolId].value
            : typeof r.values[tiToolId] !== 'undefined'
            ? r.values[tiToolId]
            : 'Threat Intelligence',
        ti_tag:
          typeof r.values[tiTagId] === 'object'
            ? r.values[tiTagId].value
            : typeof r.values[tiTagId] !== 'undefined'
            ? r.values[tiTagId]
            : 'N/A',
        ti_score:
          typeof r.values[tiScoreId] === 'object'
            ? r.values[tiScoreId].value
            : typeof r.values[tiScoreId] !== 'undefined'
            ? r.values[tiScoreId]
            : 'N/A',
        ti_permalink:
          typeof r.values[tiPermalink] === 'object'
            ? r.values[tiPermalink].value
            : typeof r.values[tiPermalink] !== 'undefined'
            ? r.values[tiPermalink]
            : ''
      };
    });

    if (this.analysisResults && this.analysisResults.length > 0) {
      this.analysisResults.unshift({
        indicator: 'All',
        ti_tool: '-',
        ti_tag: '-',
        ti_score: '-'
      });
    }
  }

  getAnalysisResultsFromLocalField() {
    if (!this.record[SOURCE_FIELD]) {
      return;
    }
    try {
      this.analysisResults =
        typeof this.record[SOURCE_FIELD] === 'string'
          ? JSON.parse(this.record[SOURCE_FIELD])
          : this.record[SOURCE_FIELD];

      this.analysisResults.unshift({
        indicator: 'All',
        ti_tool: '-',
        ti_tag: '-',
        ti_score: '-'
      });
    } catch (e) {
      console.log(e);
    }
  }

  async updateData() {
    if (this.demo) {
      this.parseDemoJsonData();
    } else if (SOURCE_TYPE === 'REFERENCED_RECORDS') {
      await this.getReferenceData();
      await this.constructKeyIdMap();
      this.getAnalysisResultsData();
    } else if (SOURCE_TYPE === 'CURRENT_RECORD') {
      this.getAnalysisResultsFromLocalField();
    }
  }

  async template() {
    if (!this.record) return;

    if (!this.demo && !this.analysisResults && this.record[SOURCE_FIELD]) await this.updateData();

    if (!this.analysisResults) return;

    if (!this.selectedIndicator) {
      this.selectedIndicator = this.analysisResults.length ? this.analysisResults[0].indicator : 'N/A';
    }

    let resultsArray = this.getSelectedResults();

    const resultsHtml = resultsArray.map(r => this.analysisResultTemplate(r));
    const options = new Set(this.analysisResults.map(result => result.indicator));

    this.exportCsv();

    return html`
      <div class="widget-container">
        <div class="dropdown-header">Indicator Selector</div>
        <div class="dropdown-container">
          <div class="selected-indicator-container" @click="${() => this.dropdownStateHandler()}">
            <div class="indicator-text selected">${this.selectedIndicator}</div>
            <div class=${this.isDropdownOpened ? 'arrow up' : 'arrow'}>${arrowIcon}</div>
          </div>
          <div class="dropdown-menu-container ${this.isDropdownOpened ? 'opened' : ''}">
            ${repeat(options, result => result.indicator, result => this.optionTemplate(result))}
          </div>
        </div>
        ${this.exportCsvButton()}
        <div class="results-box">
          <div class="analysis-results-list">
            ${resultsHtml}
          </div>
        </div>
      </div>
    `;
  }

  getSelectedResults() {
    let resultsArray = this.analysisResults.filter(r => r.indicator !== 'All');

    if (this.selectedIndicator !== 'All') {
      resultsArray = this.analysisResults.filter(result => result.indicator === this.selectedIndicator);
    }
    return resultsArray;
  }

  exportCsvButton() {
    return html`
      <a
        class="export-button"
        href="${this.encodedUri}"
        download="${this.record['tracking-id'] + ' indicator analysis results.csv'}"
        >Export</a
      >
    `;
  }

  exportCsv() {
    let csvContent = 'data:text/csv;charset=utf-8,';
    const keys = ['trackingId', 'indicator', 'ti_permalink', 'ti_tool', 'ti_tag', 'ti_score'];
    csvContent += keys.join(',') + '\r\n'; // rows needs to be in the same order
    const filteredData = this.getSelectedResults();
    filteredData.forEach(data => {
      // filter out data if key is missing from keys list
      const filtered = keys.map(key => {
        let v = data[key] || '';
        v = v.toString();
        if (v instanceof Date) {
          v = v.toLocaleString();
        }
        v = v.replace(/"/g, '""');
        if (v.search(/("|,|\n)/g) >= 0) {
          v = '"' + v + '"';
        }
        return v;
      });
      csvContent += filtered.join(',') + '\r\n';
    });
    this.encodedUri = encodeURI(csvContent);
  }

  optionTemplate(indicator) {
    if (!indicator) return '';

    const isActive = indicator === this.selectedIndicator;

    return html`
      <div
        class="${isActive ? 'option-container selected' : 'option-container'}"
        @click="${() => this.selectHandler(indicator)}"
      >
        <div class="indicator-text">${indicator}</div>
        ${isActive
          ? html`
              <div class="checkbox">${checkboxIcon}</div>
            `
          : ``}
      </div>
    `;
  }

  analysisResultTemplate(analysisResult) {
    if (!analysisResult) return '';

    const tiToolLogo =
      tiToolLogos.find(logo => logo.name === analysisResult.ti_tool.replace(/\s+/g, '').toLowerCase()) ||
      tiToolLogos.find(logo => logo.name === 'default');
    const resultIcon = resultIcons.find(icon => icon.name === analysisResult.ti_tag.toLowerCase()) || '';

    const header = analysisResult.ti_permalink
      ? html`
          <a class="record-link" href="${analysisResult.ti_permalink}" target="_blank"
            >${analysisResult.ti_tool} Results</a
          >
        `
      : html`
          ${analysisResult.ti_tool} Results
        `;

    return html`
      <div class="analysis-result-container">
        <div class="result-container">
          <div class="logo-container">
            <div class="background">
              ${tiToolLogo && isSvg(tiToolLogo.logo)
                ? tiToolLogo.logo
                : html`
                    <img src="${ifDefined(tiToolLogo.logo)}" />
                  `}
            </div>
          </div>
          <div>
            <a
              class="record-link"
              target="_blank"
              href="/record/${analysisResult.applicationId}/${analysisResult.recordId}"
              >${analysisResult.trackingId}</a
            >
            <div class="indicator-header">${analysisResult.indicator}</div>
            <div class="result-header">${header}</div>
            <div class="result-description">
              <div class="result-icon">${resultIcon.icon}</div>
              ${analysisResult.ti_tag}
            </div>
          </div>
        </div>
        <div class="score-container">
          <div class="result-header">Result Context</div>
          <div class="result-description">${analysisResult.ti_score}</div>
        </div>

        ${SOURCE_TYPE === 'REFERENCED_RECORDS' && TASKS && TASKS.length
          ? html`
              <div class="tasks-container">
                ${TASKS.map(task => {
                  return this.tasksTemplate(analysisResult, task);
                })}
              </div>
            `
          : ``}
      </div>
    `;
  }

  tasksTemplate(record, task) {
    return html`
      <button class="task-button" @click="${() => this.executeTask(record, task)}">${task.label}</button>
    `;
  }

  async executeTask(refRecord, task) {
    const record = await this.getReferencedRecord(refRecord.recordId);
    const reqBody = JSON.stringify({
      taskId: task.taskId,
      record: record
    });
    return fetch(`${this.contextData.origin}/api/task/execute/record`, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      },
      body: reqBody
    })
      .then(res => res)
      .catch(error => console.error('Error:', error));
  }

  async getReferencedRecord(recordId) {
    const referenceField = this.contextData.application.fields.find(f => f.key === SOURCE_FIELD);
    return fetch(`${location.origin}/api/app/${referenceField.targetId}/record/${recordId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${this.contextData.token}`,
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res.json();
    });
  }

  dropdownStateHandler() {
    this.isDropdownOpened = !this.isDropdownOpened;
    this.requestUpdate();
  }

  selectHandler(selectedValue) {
    this.isDropdownOpened = false;
    this.selectedIndicator = selectedValue;
    this.requestUpdate();
  }

  resizedCallback(entry) {
    /* overwrite resize callback; no longer calls requestUpdate  */
  }

  parseDemoJsonData() {
    try {
      this.analysisResults =
        typeof this.record[DEMO_FIELD_KEY] === 'string'
          ? JSON.parse(this.record[DEMO_FIELD_KEY])
          : this.record[DEMO_FIELD_KEY];
    } catch (e) {
      console.log(e);
    }

    if (this.analysisResults) {
      this.referenceTrackingFieldId = `fake-trackingField-id`;
      this.analysisResults = this.analysisResults[DEMO_MAIN_KEY];
      this.analysisResults.forEach(r => {
        r.applicationId = 'fake-application-id';
        r.recordId = 'fake-record-id';
        r.trackingId = 'TI-' + Math.floor(1000 * Math.random());
      });
      this.analysisResults.unshift({
        indicator: 'All',
        ti_tool: '-',
        ti_tag: '-',
        ti_score: '-'
      });
    }
  }
}

import { SwimlaneElement, css, html, svg } from '@swimlane/swimlane-element@1';
import { recordFrameTemplate } from '@swimlane/swimlane-element@1/templates.js';

import {
  forceSimulation,
  forceLink,
  forceManyBody,
  forceCenter
} from 'https://unpkg.com/d3-force@2.0.1/src/index.js?module';
import { scaleLinear } from 'https://unpkg.com/d3-scale@3.2.1/src/index.js?module';
import { select } from 'https://unpkg.com/d3-selection@1.4.1/src/index.js?module';

const HEIGHT = 600;
const NODE_WIDTH = 70;
const NODE_HEIGHT = 30;
const MAIN_NODE_WIDTH = 120;

export default class extends SwimlaneElement {
  static get styles() {
    return [
      super.styles,
      css`
        .frame::after {
          content: '';
        }

        .frame {
          height: ${HEIGHT}px;
        }

        select {
          position: absolute;
          text-align: center;
          top: 5px;
          left: 50%;
          transform: translate(-50%, -50%);
          border: none;
          background: none;
          color: #cdd2dd;
          font-size: 16px;
          padding: 0;
          margin: 0;
          cursor: pointer;
        }

        .graph-svg {
          width: 100%;
          height: ${HEIGHT - 40}px;
        }

        .links line {
          stroke: #505c75;
          stroke-opacity: 0.6;
        }

        .nodes rect {
          stroke: #818fa9;
          stroke-width: 1.5px;
          fill: #2b3240;
          cursor: pointer;
        }

        text {
          font-family: sans-serif;
          font-size: 10px;
          fill: #fff;
          cursor: pointer;
        }

        .centered {
          stroke: #479eff !important;
          stroke-width: 2px !important;
          cursor: default !important;
        }

        .centered text {
          cursor: default !important;
        }
      `
    ];
  }

  initialize() {
    super.initialize();
  }

  render() {
    if (!this.contextData && this.contextData.application) {
      return html``;
    }

    setTimeout(() => this.getData(), 1000);

    this.optionsList = [];
    this.fields =
      this.contextData &&
      this.contextData.application.fields.filter(f => f.fieldType === 'text' || f.selectionType === 'single' || f.fieldType === 'reference');
    this.fields.forEach(o => {
      this.optionsList.push(html`
        <option value="${o.key}">${o.name}</option>
      `);
    });
    this.selectedField = this.fields[0].key;

    return recordFrameTemplate(html`
      <select
        .value="${this.selectedField}"
        @change="${e => {
          this.selectedField = e.target.value;
          this.getData();
        }}"
      >
        ${this.optionsList}
      </select>
      <svg class="graph-svg"></svg>
    `);
  }

  draw() {
    this.width = this.renderRoot.host.clientWidth;
    this.height = HEIGHT - 40;

    this.simulation = forceSimulation()
      .force(
        'link',
        forceLink()
          .id(function(d) {
            return d.id;
          })
          .distance(150)
      )
      .force('charge', forceManyBody().strength(-700))
      .force('center', forceCenter(this.width / 2, this.height / 2));

    this.svg = select(this.renderRoot.querySelector('.graph-svg'));
    this.svg.selectAll('*').remove();

    this.link = this.svg
      .append('g')
      .attr('class', 'links')
      .selectAll('line');

    const link = this.link
      .data(this.graph.links)
      .enter()
      .append('line')
      .attr('stroke-width', 2);

    this.node = this.svg
      .append('g')
      .attr('class', 'nodes')
      .selectAll('g');

    const node = this.node
      .data(this.graph.nodes)
      .enter()
      .append('g');

    node
      .attr('title', d => `Open record ${d.label}`)
      .on('click', d => {
        if (d.id === 'value') {
          return;
        }
        this.openInNewTab(`${location.origin}/record/${this.contextData.application.id}/${d.recordId}`);
      })
      .append('rect')
      .classed('centered', d => d.id === 'value')
      .attr('width', d => (d.id === 'value' ? MAIN_NODE_WIDTH : NODE_WIDTH))
      .attr('height', NODE_HEIGHT)
      .attr('rx', 10)
      .attr('ry', 10)
      .attr(
        'transform',
        d => `translate(-${d.id === 'value' ? MAIN_NODE_WIDTH / 2 : NODE_WIDTH / 2},-${NODE_HEIGHT / 2})`
      );

    node
      .append('text')
      .text(d => {
        if (d.id === 'value') {
          let label = this.getFieldValue(this.selectedField);
          if (label.length > 20) {
            label = label.substr(0, 17) + '...';
          }
          return label;
        }
        return d.label;
      })
      .attr('x', 0)
      .attr('y', 3)
      .attr('text-anchor', 'middle');

    node.append('title').text(d => {
      return d.id;
    });

    this.simulation.nodes(this.graph.nodes).on('tick', ticked.bind(this));

    this.simulation.force('link').links(this.graph.links);

    this.init = true;
    function ticked() {
      link
        .attr('x1', d => {
          return this.boundX(d.source.x);
        })
        .attr('y1', d => {
          return this.boundY(d.source.y);
        })
        .attr('x2', d => {
          return this.boundX(d.target.x);
        })
        .attr('y2', d => {
          return this.boundY(d.target.y);
        });

      node.attr('transform', d => {
        const x = this.boundX(d.x);
        const y = this.boundY(d.y);

        return 'translate(' + x + ',' + y + ')';
      });
    }
  }

  boundX(val) {
    return Math.max(20, Math.min(this.width - 20, val));
  }

  boundY(val) {
    return Math.max(20, Math.min(this.height - 20, val));
  }

  getData() {
    if (this.searching) {
      return;
    }

    const trackingField = this.contextData.application.fields.find(f => f.key === 'tracking-id');

    this.graph = {
      nodes: [],
      links: []
    };
    if (this.svg) {
      this.svg.selectAll('*').remove();
    }

    if (this.record[this.selectedField] === undefined || this.record[this.selectedField] === '') {
      return;
    }

    const keyword = `"${this.getFieldValue(this.selectedField)}"`;
    this.searching = true;
    this.search(keyword).then(records => {
      this.searching = false;
      const recs = records.results.items;

      this.graph.nodes.push({
        id: 'value',
        label: 'VALUE'
      });

      for (const record of recs) {
        const node = {
          id: record.trackingId,
          label: record.values[trackingField.id],
          recordId: record.id
        };
        this.graph.nodes.push(node);
        this.graph.links.push({
          source: 'value',
          target: node.id
        });
      }

      this.draw();
    });
  }

  search(keyword) {
    const report = {
      keywords: keyword,
      applicationIds: [this.contextData.application.id],
      countByApplicationFacet: true,
      filters: [
        {
          value: [this.contextData.application.id],
          filterType: 'contains',
          fieldId: 'applicationId'
        }
      ]
    };
    const token = localStorage.getItem('jwt_token');
    return fetch(`${location.origin}/api/search/keyword?page=1&size=50`, {
      headers: {
        Authorization: `Bearer ${token}`,
        Accept: 'application/json, text/plain, */*',
        'Content-Type': 'application/json;charset=UTF-8'
      },
      method: 'POST',
      body: JSON.stringify(report)
    }).then(res => {
      return res.json();
    });
  }

  getFieldValue(key) {
    if (typeof this.record[key] === 'object') {
      return this.record[key].value;
    }

    return this.record[key];
  }

  openInNewTab(url) {
    var win = window.open(url, '_blank');
    win.focus();
  }
}

