export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  render() {
    if (!this.orientation) {
      this.orientation = this.record.orientation || ORIENTATION;
    }
    this.phases = this.contextData ? this.contextData.application.fields.find(field => field.key === FIELD_KEY).values : [];
    this.phaseLength = this.phases.length;
    this.activePhases = this.record && this.record[FIELD_KEY] ? this.record[FIELD_KEY].map(phase => phase.value) : [];
    return this.template();
  }

  template() {
    return html`
      <div class="widget--container ${this.orientation}">
        <div class="phases--container ${this.orientation}">
          ${repeat(this.phases, phase => phase.index, phase => this.phaseTemplate(phase))}
        </div>
      </div>
    `;
  }

  phaseTemplate(phase) {
    const isActive = this.activePhases.find(p => p === phase.name);
    return html`
    <div class="phase--container ${this.orientation}" style="${this.orientation === 'horizontal' ? `width:calc(98% / ${this.phaseLength})`: `height:calc(100% / ${this.phaseLength})`}">
      <div class="phase-circle--container ${this.orientation}">
          <div class="${isActive ? 'phase-circle active': 'phase-circle'}" @click="${() => this.selectPhase(phase)}"> ${isActive ? checkMarkIcon : ''}</div>
        </div>
        <div class="phase-label--container ${this.orientation}">
          <div class="${isActive ? 'phase-label active' : `phase-label ${this.orientation}`}">${phase.name}</div>
        </div>
      </div>
    `;
  }

  selectPhase(phase) {
    const idx = this.activePhases.findIndex(p => p === phase.name);
    if (idx > -1) {
      this.activePhases.splice(idx, 1);
    } else {
      this.activePhases.push(phase.name);
    }
    this.updateRecordValue(FIELD_KEY, this.activePhases);
  }
}

