    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    this.init();
  }

  disconnectedCallback() {
  }

  attributeChangedCallback(attrName, oldVal, newVal) {
    this.update();
  }

  get record() {
    return JSON.parse(this.getAttribute('record'));
  }

  set record(value) {
