export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  initialize() {
    super.initialize();
  }

  resizedCallback(entry) {
    /** overwrite resize callback; no longer calls requestupdate */
  }

  render() {
    if (this.record[DEMO_FIELD_KEY]) {
      this.history = this.record[DEMO_FIELD_KEY];
    } else if (!this.record[FIELD_KEY] || !this.record) {
      return this.defaultTemplate();
    } else {
      this.parseJsonData();
    }

    return this.mainTemplate();
  }

  parseJsonData() {
    try {
      chatApp = this.record[CHAT_APP_KEY] ? this.record[CHAT_APP_KEY] : 'Slack';
      this.history =
        typeof this.record[FIELD_KEY] === 'string' ? JSON.parse(this.record[FIELD_KEY]) : this.record[FIELD_KEY];
    } catch {
      return this.defaultTemplate();
    }
  }

  //Count total of msgs including replies
  countTotalMsgs(jsonData) {
    if (!jsonData || !jsonData.length) {
      return 0;
    }
    let totalCount = 0;

    for (const msgObject of jsonData) {
      if (Array.isArray(msgObject.replies)) {
        totalCount += msgObject.replies.length;
      }
    }

    totalCount += jsonData.length;
    this.requestUpdate();
    return totalCount;
  }

  //Sort array of msgs obj by asc date and time
  sortByDate(arrOfMsgs) {
    return arrOfMsgs.sort((a, b) => new Date(a.created_date_time) - new Date(b.created_date_time));
  }
  // group messages by same date
  groupMsgsByDate(arrOfMsgs) {
    const resultsObj = {};
    arrOfMsgs.forEach(msg => {
      const options = { year: 'numeric', month: 'numeric', day: 'numeric' };
      const date = new Date(msg.created_date_time).toLocaleDateString('en-US', options);
      if (!resultsObj[date]) {
        resultsObj[date] = [msg];
      } else {
        resultsObj[date].push(msg);
      }
    });
    return resultsObj;
  }

  convertTimeToHoursAndMin(dateString) {
    const options = {
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    };
    //ex: 1:00PM
    return new Date(dateString).toLocaleString('en-US', options);
  }

  //Convert full hour and date string for each reply msg
  convertTimeToHoursAndMinPlusDateStr(dateString) {
    const options = { year: 'numeric', month: 'numeric', day: 'numeric' };
    //ex: 2020/12/28 at 23:14
    return `${new Date(dateString).toLocaleDateString('en-US', options)} at ${this.convertTimeToHoursAndMin(
      dateString
    )}`;
  }

  //get complete date sentence
  convertDateStr(date) {
    const options = { weekday: 'long', month: 'long', day: 'numeric' };
    const today = new Date();
    const dateInput = new Date(date);
    if (today.toDateString() === dateInput.toDateString()) return 'Today';
    // ex: Monday, December 28th
    return `${dateInput.toLocaleDateString('en-US', options)}${this.addDayOrdinal(dateInput.getDate())}`;
  }

  //add ordinal to day number
  addDayOrdinal(num) {
    if (num === 1 || num === 21 || num === 31) {
      return 'st';
    } else if (num === 2 || num === 22) {
      return 'nd';
    } else if (num === 3 || num === 23) {
      return 'rd';
    } else {
      return 'th';
    }
  }

  msgCountTemplate() {
    const msgCount = this.countTotalMsgs(this.history);
    return html`
      ${msgCount > 0
        ? html`
            <p class="msg-count">${msgCount} items in channel history</p>
          `
        : html`
            <p class="no-data">No history data available</p>
          `}
    `;
  }

  msgTemplate(message) {
    let replies = false;
    let sortedRepliesArray;
    let imagesInMainMsg = false;

    if (!message) {
      return html``;
    }

    if (
      (Array.isArray(message.replies) && message.replies.length) ||
      (typeof message.replies === 'object' && message.replies !== null)
    ) {
      replies = true;
      sortedRepliesArray = this.sortByDate(message.replies);
    }

    if (
      (Array.isArray(message.files) && message.files.length) ||
      (typeof message.files === 'object' && message.files !== null)
    ) {
      imagesInMainMsg = true;
    }

    return html`
      <div class="primary-msg">
        <div class="msg--container">
          <div class="profile-image-name-hour--container">
            <div class="profile-img-outer-circle">
              <div class="profile-img-inner-circle">
                <div class="acronym">${message.from[0]}</div>
              </div>
            </div>
            <div class="user-name">
              ${message.from}
            </div>
            <span class="time-in-hours">
              ${this.convertTimeToHoursAndMin(message.created_date_time)}
            </span>
          </div>
          <p>${message.body}</p>
          <div>
            ${imagesInMainMsg ? repeat(message.files, img => img[0], img => this.imageTemplate(img)) : html``}
          </div>
        </div>
        <div class="replies-container">
          <div class="replies-length--wrapper">
            <div class="${replies ? 'replies-length--container' : ''}">
              ${replies
                ? html`
                    <span class="replies-label"
                      >${message.replies.length > 1
                        ? html`
                            ${message.replies.length} Replies Shown Below
                          `
                        : html`
                            ${message.replies.length} Reply Shown Below
                          `}</span
                    >
                  `
                : html``}
            </div>
          </div>
          <div>
            ${replies
              ? repeat(sortedRepliesArray, reply => reply.created_date_time, reply => this.replyTemplate(reply))
              : html``}
          </div>
        </div>
      </div>
    `;
  }
  replyTemplate(reply) {
    let imgsInReplies = false;
    if (
      (Array.isArray(reply.files) && reply.files.length) ||
      (typeof reply.files === 'object' && reply.files !== null)
    ) {
      imgsInReplies = true;
    }
    return html`
      <div class="reply-msg--wrapper">
        <div class="reply-msg--container">
          <div class="profile-image-name-hour--container">
            <div class="profile-img-outer-circle">
              <div class="profile-img-inner-circle">
                <div class="acronym">${reply.from[0]}</div>
              </div>
            </div>
            <div class="user-name">
              ${reply.from}
            </div>
            <span class="time-in-hours">
              ${this.convertTimeToHoursAndMinPlusDateStr(reply.created_date_time)}
            </span>
          </div>
          <p>${reply.body}</p>
          <div>
            ${imgsInReplies ? repeat(reply.files, img => img[0], img => this.imageTemplate(img)) : html``}
          </div>
        </div>
      </div>
    `;
  }

  imageTemplate(img) {
    const imgBase64 = 'data:image/jpeg;charset=utf-8;base64, ' + img;
    return html`
      <div>
        <img src=${imgBase64} alt="" />
      </div>
    `;
  }

  mainTemplate() {
    if (!this.history) {
      return this.defaultTemplate();
    }
    const primaryMsgsList = this.sortByDate(this.history);
    const msgsListGroupedByDateObj = this.groupMsgsByDate(primaryMsgsList);
    const dateKeysArray = Object.keys(msgsListGroupedByDateObj);
    return html`
      <div class="chat-history--main-container">
        <div class="widget-title--container">
          <h4 class="widget-title">${chatApp} Channel History</h4>
        </div>
        <div class="channel-history-wrapper">
          <div>
            <h2 class="main-title">Channel History</h2>
            <p class="subtitle">Check in on the ${chatApp} channel created for this ticket</p>
          </div>
          <div class="msg-count--main-container">
            <div class="chat-icon--container">
              ${conversationIcon}
            </div>
            <div class="msg-count--container">
              <span class="secondary-title">Channel History</span>
              ${this.msgCountTemplate()}
            </div>
          </div>
          <div>
            ${repeat(dateKeysArray, date => date, date => this.msgGroupTemplate(msgsListGroupedByDateObj[date], date))}
          </div>
        </div>
      </div>
    `;
  }

  msgGroupTemplate(primaryMsgsList, date) {
    return html`
      <div>
        <div class="primary-date--container">
          <div class="date-separator">
            ${this.convertDateStr(date)}
          </div>
          <hr class="date-horizontal-line-separator" />
        </div>
        <div>
          ${repeat(primaryMsgsList, primaryMsg => primaryMsg.web_url, primaryMsg => this.msgTemplate(primaryMsg))}
        </div>
      </div>
    `;
  }
  defaultTemplate() {
    return html`
      <div class="chat-history--main-container">
        <div class="widget-title--container">
          <h4 class="widget-title">${chatApp} Channel History</h4>
        </div>
        <div class="channel-history-wrapper">
          <div>
            <h2 class="main-title">Channel History</h2>
            <p class="subtitle">Check in on the ${chatApp} channel created for this ticket</p>
          </div>
          <div class="msg-count--main-container">
            <div class="chat-icon--container">
              ${conversationIcon}
            </div>
            <div class="msg-count--container">
              <span class="secondary-title">Channel History</span>
              <p class="no-data">No history data available</p>
            </div>
          </div>
        </div>
      </div>
    `;
  }
}

