function dateTimeDiff(start, end) {
  let diff = end.getTime() - start.getTime();
  const sec = diff / 1000;
  const min = sec / 60;
  const hr = min / 60;
  const day = hr / 24;
  const week = day / 7;
  const month = week / 4;
  const year = month / 12;

  const plural = function(num) {
    return num >= 2 ? 's' : '';
  };

  if (year >= 1) {
    const roundedYear = Math.round(year);
    return `${roundedYear} Year${plural(roundedYear)}`;
  } else if (month >= 1) {
    const roundedMonth = Math.round(month);
    return `${roundedMonth} Month${plural(roundedMonth)}`;
  } else if (week >= 1) {
    const roundedWeek = Math.round(week);
    return `${roundedWeek} Week${plural(roundedWeek)}`;
  } else if (day >= 1) {
    const roundedDay = Math.round(day);
    return `${roundedDay} Day${plural(roundedDay)}`;
  } else if (hr >= 1) {
    const roundedHr = Math.round(hr);
    return `${roundedHr} Hour${plural(roundedHr)}`;
  } else if (min >= 1) {
    const roundedMin = Math.round(min);
    return `${roundedMin} Minute${plural(roundedMin)}`;
  } else if (sec >= 1) {
    const roundedSec = Math.round(sec);
    return `${roundedSec} Second${plural(roundedSec)}`;
  }

  if (checkNonNumberValue(diff)) {
    return '-';
  } else if (diff < 0) {
    diff = 0;
  }

  return `${diff} Millisecond${plural(diff)}`;
}

function checkNonNumberValue(value) {
  return isNaN(value) || value == 'Infinity' || value == '-Infinity';
}

function timePartitionTemplate(partitions, i, totalTime) {
  const partition = partitions[i];

  const partitionStartDate = new Date(partition.start);
  let partitionEndDate = new Date(partition.end);


  if (partitionStartDate && !partitionEndDate) {
    partitionEndDate = currentDate;
  }

  if (partitionEndDate && partitionEndDate.getTime() > currentDate.getTime()) {
    partitionEndDate = currentDate;
  }

  const partTime = partitionEndDate.getTime() - partitionStartDate.getTime();
  let percentUsage = Math.round((partTime / totalTime) * 100);
  let diff = dateTimeDiff(partitionStartDate, partitionEndDate);

  if (percentUsage < 0) {
    percentUsage = 0;
  }
  if (checkNonNumberValue(percentUsage)) {
    percentUsage = '-';
  }
  if (
    (isNaN(diff) && (typeof diff !== 'string' && !diff instanceof String)) ||
    diff == 'Infinity' ||
    diff == '-Infinity'
  ) {
    diff = 0;
  }

  return html`
    <div class="time-partitions--partition">
      <div class="partition--circle">
        <div class="circle" style="background-color: ${partition.color}">
          <div class="inner-circle"></div>
        </div>
      </div>

      <div class="partition--text">
        <div class="partition--title" style="color: ${partition.color};">
          ${i + 1}. ${partition.label}
        </div>

        <div class="partition--subtitle">
          ${diff} / ${percentUsage}%
        </div>
      </div>
    </div>
  `;
}

function timespanTemplate(partitions, totalTime) {
  const diff = dateTimeDiff(partitions[0].start, partitions[partitions.length - 1].end);
  const timeToCurrent = currentDate.getTime() - partitions[0].start.getTime();
  const currentTimePercent = Math.round((timeToCurrent / totalTime) * 100);

  return html`
    <div class="timespan--text">
      <div class="timespan--title">${label}</div>
      <div class="timespan--subtitle">${diff}</div>
    </div>

    <div class="timespan--lines">
      <div class="line--vertical center"></div>
      <div class="line--horizontal"></div>
      <div class="timespan--bottom-lines">
        <div class="line--vertical"></div>
        <div class="line--vertical"></div>
      </div>
    </div>

    <div class="timespan--progressbar">
      ${partitions.map((partition, i) => {
        const partitionStartDate = new Date(partition.start);
        let partitionEndDate = new Date(partition.end);

        if (partitionStartDate && !partitionEndDate) {
          partitionEndDate = currentDate;
        }

        if (partitionEndDate && partitionEndDate.getTime() > currentDate.getTime()) {
          partitionEndDate = currentDate;
        }

        const partTime = partitionEndDate.getTime() - partitionStartDate.getTime();
        const percentUsage = Math.floor((partTime / totalTime) * 100);

        if (isNaN(partTime)) {
          return ``;
        }

        return html`
          <div class="progressbar--partition" style="background-color: ${partition.color}; width: ${percentUsage}%;">
            <div class="partition--index">${i + 1}</div>
          </div>
        `;
      })}
      <div
        class="progressbar--current-time"
        style="left: ${currentTimePercent}%; display: ${currentTimePercent >= 100
          ? 'none'
          : 'initial'}; background-color: ${currentDateColor}"
      >
        <div class="current-time--label">Now</div>
      </div>
    </div>
  `;
}

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  render() {
    if (!(this.record && Array.isArray(partitionsConfig) && partitionsConfig.length)) return html``;
    this.partitions = JSON.parse(JSON.stringify(partitionsConfig));

    const startDateField = this.partitions[0].startDateField;
    const endDateField = this.partitions[this.partitions.length - 1].endDateField || 'timespan-widget-current-date';

    this.partitions.forEach(_case => {
      if (this.record[_case.startDateField]) {
        _case.start = new Date(this.record[_case.startDateField]);
      }
      if (this.record[_case.endDateField]) {
        _case.end = new Date(this.record[_case.endDateField]);
      }
    });

    if (masterStartDate && !this.record[startDateField]) {
      this.partitions[0].start = new Date(masterStartDate);
    }

    if (!this.record[endDateField]) {
      this.partitions[this.partitions.length - 1].end = currentDate;
    }

    const totalTime = this.partitions[this.partitions.length - 1].end.getTime() - this.partitions[0].start.getTime();

    return html`
      <div class="time-partitions--title">${windowTitle}</div>

      <div class="time-partitions--timespan">
        ${timespanTemplate(this.partitions, totalTime)}
      </div>

      <div class="time-partitions--partitions">
        ${this.partitions.map((_, i) => timePartitionTemplate(this.partitions, i, totalTime))}
      </div>
    `;
  }
}

import { SwimlaneElement, css, html, svg } from '@swimlane/swimlane-element@1';
import { recordFrameTemplate } from '@swimlane/swimlane-element@1/templates.js';

export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, css`
      * {
        border: 0 !important;
        margin: 0 !important;
        padding: 0 !important;
        box-shadow: none !important;
      }


      .frame::after {
        content: " ";
      }
    `];
  }

  render() {
    return recordFrameTemplate(html`
    <p><b>Current Owner:</b></p>
      <pre>${this.record['case_current-owner'].name}</pre>
      <pre>${this.record['case_current-owner'].id}</pre>
    `);
  }
}

values.description:
Adversaries may abuse shared modules to execute malicious payloads. The Windows module loader can be instructed to load DLLs from arbitrary local paths and arbitrary Universal Naming Convention (UNC) network paths. This functionality resides in NTDLL.dll and is part of the Windows [Native API](https://attack.mitre.org/techniques/T1106) which is called from functions like <code>CreateProcess</code>, <code>LoadLibrary</code>, etc. of the Win32 API. (Citation: Wikipedia Windows Library Files)

The module loader can load DLLs:

* via specification of the (fully-qualified or relative) DLL pathname in the IMPORT directory;

* via EXPORT forwarded to another DLL, specified with (fully-qualified or relative) pathname (but without extension);

* via an NTFS junction or symlink program.exe.local with the fully-qualified or relative pathname of a directory containing the DLLs specified in the IMPORT directory or forwarded EXPORTs;

* via <code>&#x3c;file name="filename.extension" loadFrom="fully-qualified or relative pathname"&#x3e;</code> in an embedded or external "application manifest". The file name refers to an entry in the IMPORT directory or a forwarded EXPORT.

Adversaries may use this functionality as a way to execute arbitrary code on a victim system. For example, malware may execute share modules to load additional components or features.
