    transition: all 0.3s ease-in-out;
    box-sizing: border-box;
    background: #262b37;
    height: 35px;
    border-bottom: 1px solid #455066;
  }

  .alert-text {
    font-family: Source Sans Pro;
    font-size: 16px;
    font-weight: 400;
    color: #ebedf2;
    margin: 0 12px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .alert-text.selected {
    color: #818fa9;
    width: calc(100%);
  }

  .selected-alert-container .arrow {
    margin-right: 10px;
  }

  .selected-alert-container .arrow.up svg {
    transform: rotate(180deg);
  }

  /* dropdown menu */
  .dropdown-menu-container {
    width: 100%;
    overflow-y: auto;
    overflow-x: hidden;
    border: 1px solid #2b3240;
    box-sizing: border-box;
    background: #313847;
    opacity: 0;
    transition: all 0.3s ease;
    position: absolute; // need to be placed above possible elements below
    z-index: 9999;
    display: none;
    visibility: hidden;
    overflow-y: hidden;
  }

  .dropdown-menu-container.opened {
    opacity: 1;
    z-index: 9999;
    display: block;
    visibility: visible;
    cursor: pointer;
  }

  .option-list {
    max-height: 275px;
    overflow-y: auto;
  }

  .option-list::-webkit-scrollbar-thumb {
    background: #455066;
    border: 7px solid #313847;
    border-radius: 10px;
    min-height: 25%;
  }

  .option-container {
    border-bottom: 1px solid #455066;
    padding: 7px 0;
    transition: all 0.3s ease-in-out;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  .option-container.selected {
    background-color: #171a21;
  }
  .option-container:last-child {
    border-bottom: none;
  }
  .option-container:hover {
    background: #1c2029;
  }

  .dropdown-search {
    display: flex;
    position: relative;
    margin: 5px;
    align-items: center;
    background: #455066;
    height: 31px;
  }

  .dropdown-search input {
    flex-grow: 1;
