export default class extends SwimlaneElement {
  static get styles() {
    return [super.styles, widgetCss];
  }

  formatPluginName(name) {
    if (!name) {
      return '';
    }

    const comparedValue = name[0].toUpperCase() + name.slice(1);

    if (comparedValue === 'Jira') {
      return 'Jira';
    } else if (comparedValue === 'ServiceNow') {
      return 'ServiceNow';
    } else {
      return '';
    }
  }

  render() {
    if (!this.record || !this.record[FIELD_KEY1] || !this.record[FIELD_KEY2]) {
      this.ticketSummary = 'No ticket summary has been received.';
      this.ticketNumber = 'N/A';
    } else {
      this.ticketSummary = this.record[FIELD_KEY1];
      this.ticketNumber = this.record[FIELD_KEY2];
      this.ticketingPlugin = this.formatPluginName(this.record[FIELD_KEY3]);
    }

    return this.template();
  }

  template() {
    const match = ticketingPluginList.find(option => option.name === this.ticketingPlugin);
    console.log('match', match);
    return html`
      <div class="main-container">
        <div class="icon-container">
          ${match ? match.icon : ''}
        </div>
        <div class="description-container">
          <div class="title">Ticket Summary</div>
          <div class="summary-content">
            ${this.ticketSummary}
          </div>
        </div>
        <div class="ticket-id-key-container">
          <div class="title">
            Ticket Key
          </div>
          <div class="ticket-id-content">
            ${this.ticketNumber}
          </div>
        </div>
      </div>
    `;
  }
}

