import { SwimlaneElement, html, css } from '@swimlane/swimlane-element@1';
// Window title eg. "Task Time Distribution"
const windowTitle = 'Task Time Distribution';
// The label for your overall measured time period from start to finish, eg: "Dwell Time"
const label = 'Dwell Time';
// If you do not have a field corresponding to the end date of the period you're tracking, the current date will act as the closing boundary
const currentDate = new Date();
// color for the current date tick on the timeline
const currentDateColor = 'red';

// range of total time. masterStartDate must come before masterEndDate.
// if very first start date and very last end date aren't provided, these values will be used.
const masterStartDate = '2021-05-04T14:30:52.380Z';
const masterEndDate = '2022-05-05T14:30:52.380Z';
// Key from your record for the partition for events that you wish to partition. For each event in the interval, you define an object with a label for the event,
// a hex or rgb value for the color you wish to use to render that event, and the fields from the record that will have ISO date values representing the start and end times.
// If the last interval does not have corresponding field for an end value, you need to specify a 'fallbackEndDate' property with a field value so that the widget can render
// a bound graph. Here's an entry as an example:
